import React, { useEffect, useState } from 'react'
import {useNavigate} from "react-router-dom";
import { getUser } from '../user';

const Login = () => {

  const user =getUser();
  const navigate = useNavigate();
  const [params,setParams] = useState({});
  const [errors,setErrors] = useState({});

const handleChange =(e) =>{
  const {name,value} = e.target;
setParams(prev =>{
  return{
    ...prev,
    [name]: value
  }
})


setError(name, "");
setError("not  registered", "");
if(name === "password"){
  setError("incorrect_password", "");
}


}

useEffect(()=>{
  if(user){
    navigate("/events");
  }
},[]);



const setError =(name,value) =>{
  setErrors(prev =>{
    return {
      ...prev,
      [name]: value
    }
  })
}

const login =() =>{
  const {email,password} = params;

if(!email){
  setError("email", "email is required");
}
if(!password){
setError("password","password is required");
}
if(email && password){
  const users= [];

  users.push(...JSON.parse(localStorage.getItem("users"))|| []);

  const exist = users.find()
  if(exist){
    if(exist.password !== params.password)
{
  setError("incorrect_password", "Incorrect Passwod");

}else{
  exist.login = true;
  localStorage.setItem("users",JSON.stringify(users));
  navigate("/events");
  setParams({});
}  }
else{
  setError("not_registered","User not Registered");
}
}
}

  return (
    <div className='card'>
      <div className='form'>
        <div className='title'>
          <p>Login</p>
        </div>
        {!!errors.not_registered  && <span className='mandatory'>{errors.not_registered}</span>}
        <div className='space'></div>
        <div>
          <label>Email</label>
          <input type="text" name="email" value={params.email || ""} onChange ={handleChange}/>
          {!! errors.email && <span className='mandatory'>{errors.email}</span>}
        </div>
        <div className='space'></div>
        <div>
          <label>Password</label>
          <input type="password" name ="password" value = {params.password || ""} onChange={handleChange}
          />
          {!!errors.password && <span className='mandatory'>{errors.password}</span>}
          {!! errors.incorrect_password && <span className='manatory'>{errors.incorrect_password}</span> }
        </div>
        <div className='space'></div>
        <div>
          <span>Not Registered ? <span className='account' onClick={() => navigate("/register")}>Create an account
          </span></span>
        </div>
      </div>
    </div>
  )
}

export default Login
